{
    "displayName": "sampleAlertingProfile",
    "mzId": "1",
    "rules": [
        {
            "severityLevel": "AVAILABILITY",
            "tagFilter": {
                "includeMode": "INCLUDE_ALL",
                "tagFilters": [
                    {
                        "context": "AWS",
                        "key": "tagKey",
                        "value": "tagValue"
                    }
                ]
            },
            "delayInMinutes": 60
        }
    ],
    "eventTypeFilters": [
        {
            "predefinedEventFilter": {
                "eventType": "OSI_HIGH_CPU",
                "negate": true
            }
        },
        {
            "customEventFilter": {
                "customTitleFilter": {
                    "enabled": true,
                    "value": "filterValue",
                    "operator": "EQUALS",
                    "negate": false,
                    "caseInsensitive": true
                },
                "customDescriptionFilter": {
                    "enabled": false,
                    "value": "filterValue",
                    "operator": "CONTAINS",
                    "negate": true,
                    "caseInsensitive": false
                }
            }
        }
    ]
}

